import Header from './Header'
import React from 'react';
import { Link, withRouter } from 'react-router-dom';
import Logo from './Logo.js';
import * as duckAuth from '../duckAuth.js';
import './styles/Login.css';

class Login extends React.Component {
  constructor(props){
    super(props);
    this.state = {
      email: '',
      password: ''
    }
    this.handleChange = this.handleChange.bind(this);
    this.handleSubmit = this.handleSubmit.bind(this);

  }
  handleChange(e) {
    const {name, value} = e.target;
    this.setState({
      [name]: value 
    });
  }
  handleSubmit(e){
        e.preventDefault()
    if (!this.state.email || !this.state.password){
      return;
    }
    // здесь авторизуем пользователя
    // далее проверяем токен
    // наконец, перенаправляем пользователя на страницу `/ducks`

  }

render() {
  return(
    <section className="login">
      <Header to='/sign-up' text='Регистрация' />
      <div className="login__container">
        <h2 className="login__title">Вход</h2>
        <form className="login__form" name="login" onSubmit='' noValidate>
          <label className="login__form-field">
            <input required onChange='' id="url-login" type="Email" name="Email" placeholder="Email" className="login__placeholder" minLength="2" maxLength="40"/>
            <span className="email-input-error login__placeholder-error"></span>
          </label>
          <label className="login__form-field">
            <input required onChange='' id="password-input" type="password" name="password" placeholder="Пароль" className="login__placeholder" minLength="6" maxLength="20"/>
            <span className="info-input-error login__placeholder-error"></span>
          </label>
          <button type="submit" name="login" className="login__button">Войти</button>
        </form>
      </div>
    </section>
    )
  }
}

export default withRouter(Login);
