import React from 'react'
import api from '../utils/Api'

function Main(props) {
  const [userName, setUserName] = React.useState()
  const [userDescription, setUserDescription] = React.useState()
  const [userAvatar, setUserAvatar] = React.useState()

  React.useEffect(() => {
    function renderUserInfo(name, description, avatar) {
      setUserName(name)
      setUserDescription(description)
      setUserAvatar(avatar)
    }
  })

  return (
    <main>
    <section className="profile-columns">
      <div className="profile">
        <img src="" alt="Аватар" className="profile__avatar" onClick={props.onEditProfile} style={{ backgroundImage: `url(${userAvatar})` }}/>
        <img src="<%=require('../images/edit.svg')%>" alt="Правка" className="profile__edit-icon"/>
        <div className="profile__info">
          <div className="profile__header">
            <h1 className="profile__title">{userName}</h1>
            <button type="button" className="profile__edit-button" aria-label="Редактировать" onClick={props.onEditAvatar}></button>
          </div>
          <p className="profile__subtitle">{userDescription}</p>
        </div>
      </div>
      <button type="button" className="profile-columns__add-button" onClick={props.onAddPlace}></button>
    </section>
    <section className="elements">
    </section>
  </main>
  )
}
export default Main
