import '../index.css'
import Header from '../components/Header'
import Main from '../components/Main'
import Footer from '../components/Footer'

function App() {
  const [isEditProfilePopupOpen, setProfilePopupOpen] = React.useState(false)
  const [isAddPlacePopupOpen, setPlacePopupOpen] = React.useState(false)
  const [isEditAvatarPopupOpen, setAvatarPopupOpen] = React.useState(false)

  function handleEditAvatarClick() {
    setAvatarPopupOpen(!isEditAvatarPopupOpen)
  }

  function handleEditProfileClick() {
    setProfilePopupOpen(!isEditProfilePopupOpen)
  }

  function handleAddPlaceClick() {
    setPlacePopupOpen(!isAddPlacePopupOpen)
  }
  return (
  <>
  <div className="page">
  <Header />
  <Main onEditProfile={handleEditProfileClick} onAddPlace={handleAddPlaceClick} onEditAvatar={handleEditAvatarClick}/>
  <Footer />
  <PopupWithForm isOpen={isEditProfilePopupOpen} name="" title="" />
  <PopupWithForm isOpen={isAddPlacePopupOpen} name="" title="" />
  <PopupWithForm isOpen={isEditAvatarPopupOpen} name="" title="" />
    </div>
    <template className="element-template element-template_type_card">
      <div className="element">
        <button type="button" className="element__delete-button"></button>
        <img src="#" alt="Фотография" className="element__photo"/>
        <div className="element__label">
          <h2 className="element__title"> </h2>
          <div className="element__like">
            <button type="button" className="element__like-button"></button>
            <p className="element__like-counter"> 0 </p>
          </div>
        </div>
      </div>
    </template>
    </>
  );
}

export default App;
