import React from 'react'
import Header from '../components/Header'
import Main from '../components/Main'
import Footer from '../components/Footer'
import PopupWithForm from './PopupWithForm'
import ImagePopup from './ImagePopup'
import api from '../utils/api'
import EditProfilePopup from './EditProfilePopup'
import { CurrentUserContext } from '../contexts/CurrentUserContext'
import EditAvatarPopup from './EditAvatarPopup'

function App() {
  const [isEditProfilePopupOpen, setProfilePopupOpen] = React.useState(false)
  const [isAddPlacePopupOpen, setPlacePopupOpen] = React.useState(false)
  const [isEditAvatarPopupOpen, setAvatarPopupOpen] = React.useState(false)
  const [selectedCard, setSelectedCard] = React.useState({})
  const [currentUser, setCurrentUser] = React.useState([])
  const [cards, setCards] = React.useState([])

  function handleEditAvatarClick() {
    setAvatarPopupOpen(!isEditAvatarPopupOpen)
  }

  function handleEditProfileClick() {
    setProfilePopupOpen(!isEditProfilePopupOpen)
  }

  function handleAddPlaceClick() {
    setPlacePopupOpen(!isAddPlacePopupOpen)
  }

  function handleCardClick(card) {
    setSelectedCard(card)
  }  

  function handleUpdateUser(info) {
    api.editProfileInfo(info)
      .then((data) => {
        setCurrentUser(data)
        closeAllPopups()
      })
      .catch(err => Promise.reject(err))
  }

  function handleUpdateAvatar(data) {
    api.changeAvatar(data.avatar)
      .then((info) => {
        setCurrentUser(info)
        closeAllPopups()
      })
      .catch(err => Promise.reject(err))
  }

  function handleCardLike(card) {
    // Снова проверяем, есть ли уже лайк на этой карточке
    const isLiked = card.likes.some(i => i._id === currentUser._id);

    // Отправляем запрос в API и получаем обновлённые данные карточки
    api.changeLikeCardStatus(card._id, !isLiked)
      .then((newCard) => {
        setCards((state) => state.map((c) => c._id === card._id ? newCard : c));
    });
} 

  function handleCardDelete(card) {
    api.removeCard(card._id)
      .then((data) => {
        cards.filter((card) => card._id !== data._id)
      })
  }

  function closeAllPopups() {
    setAvatarPopupOpen(false)
    setProfilePopupOpen(false)
    setPlacePopupOpen(false)
    setSelectedCard({})
  }

  React.useEffect(() => {
      api.getProfileInfo()
        .then((data) => {
          setCurrentUser(data)
        })
        .catch(err => Promise.reject(err))
    }, [])

    React.useEffect(() => {
      api.getInitialCards()
        .then((items) => {
          setCards(items)
      })
        .catch(err => Promise.reject(err))
  }, [])

  return (
    <CurrentUserContext.Provider value={currentUser}>
  <div className="page">
  <Header />
  <Main cards={cards} onCardLike={handleCardLike} onCardDelete={handleCardDelete} onEditProfile={handleEditProfileClick} onAddPlace={handleAddPlaceClick} onEditAvatar={handleEditAvatarClick} onCardClick={handleCardClick}/>
  <Footer />
  <EditProfilePopup isOpen={isEditProfilePopupOpen} onClose={closeAllPopups} onUpdateUser={handleUpdateUser} />
  <AddPlacePopup isOpen={isAddPlacePopupOpen} onClose={closeAllPopups} />
  <EditAvatarPopup isOpen={isEditAvatarPopupOpen} onClose={closeAllPopups} onUpdateAvatar={handleUpdateAvatar} />
  <ImagePopup card = {selectedCard} onClose = {closeAllPopups}/>
  </div>
    </CurrentUserContext.Provider>
  );
}

export default App;
