import Header from './Header'
import React from 'react'
import * as Auth from '../Auth.js'

function Register() {
const [inputs, setInputs] = React.useState({
  email: '',
  password: ''
})

function handleChange(e) {
  const {name, value} = e.target;
  this.setState({
    [name]: value 
  });
}

function handleSubmit(e) {
      e.preventDefault()
      const { email, password } = inputs;
      Auth.register(email, password)
}

  return(
    <section className="login">
      <Header to='/sign-in' text='Войти' />
      <div className="login__container">
        <h2 className="login__title">Регистрация</h2>
        <form className="login__form" name="login" onSubmit='' noValidate>
          <label className="login__form-field">
            <input required onChange='' id="url-login" type="Email" name="Email" placeholder="Email" className="login__placeholder" minLength="2" maxLength="40"/>
            <span className="email-input-error login__placeholder-error"></span>
          </label>
          <label className="login__form-field">
            <input required onChange='' id="password-input" type="password" name="password" placeholder="Пароль" className="login__placeholder" minLength="6" maxLength="20"/>
            <span className="info-input-error login__placeholder-error"></span>
          </label>
          <button type="submit" name="login" className="login__button">Зарегистрироваться</button>
          <p className="login__text">Уже зарегистрированы? <a href='/' className="login__link">Войти</a></p>
        </form>
      </div>
    </section>
  )
}

export default Register
